import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({

    inputs: {

        flex: 1.8,
        width: '80%',
    },

    campoInput: {

        flexDirection: 'column',
        marginTop: 20

    },

    textInput: {

        color: '#fff',
        paddingVertical: 5,
        fontSize: 16
    },

    input: {

        width: '100%',
        height : 60,
        backgroundColor: '#121212',
        color: '#666',
        borderColor: '#666',
        borderWidth: 3,
        borderRadius: 4,
        paddingLeft: 12,
        position: 'relative',
    }
})
